<template>
  <div
      v-if="error"
      "class="`rounded md:rounded-lg lg:rounded-lg bg-gray-400 blur-md ${dimensions}`"
  />            
  <img
    v-else
    ref="img"
    loading="lazy"
    src="data:image/jpeg;base64,"
    :alt="alt"
    @load="onLoaded"
    @error="setError"
    :class="`object-cover rounded md:rounded-lg lg:rounded-lg w-36 h-20 md:w-72 md:h-44 lg:w-72 lg:h-44 ${dimensions}`"
  />        
</template>

<script>
import {
  defineComponent,  
} from '@nuxtjs/composition-api'
import { useLoadedFlag } from '../../composables/is-loaded'
export default defineComponent({
  name: 'Img',    
  props: {
    src: {
      type: String,
      default: () => '',
    },
    mode: {
      type: String,
      default: () => 'responsive',
    },
    height: {
      type: String,
      default: () => "600",
    },
    width: {
      type: String,
      default: () => "800",
    },
    loading: {
      type: String,
      default: () => 'lazy',
    },
    alt: {
      type: String,
      default: () => 'fox now',
    },
    dimensions: {
      type: String,
      default: () => '',
    }
  },
  setup (props) {
    const { error, setError } = useLoadedFlag()    
    return {        
        error,
        setError
    }
  },
  methods: {
      onLoaded: function (e) {
        if(this.$refs.img.src !== this.$props.src) {
            this.$refs.img.src = this.$props.src
        }
      }
  }
})
</script>